import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)



export default new Vuex.Store({
  state: {
    count: 0
  },
  mutations: {
    increment(state){
      state.count ++ ;
    },
    decrease (state){
      state.count --;
    }
  },
  getters: {
    getState(state){
      return state.count >0? state.count : 0;
    }
  }
})
